import React, {useState} from "react";
import Header from "./Header";
import Card from "./Card";
import { useLocation } from "react-router";



function Expenses() {
    const location = useLocation();
    const name = location.state.name;
    const month_index = location.month_index;
    const [expenses, setExpenses] = useState([]);

    async function fetchData(){
        let response = await fetch('http://localhost:8080/127.0.0.1:3100/');

        if (response.status === 200) {
            let data = await response.text();
            let jsonData = JSON.parse(data);

            for (let i = 0; i < jsonData.length ; i++ ){
                if(jsonData[i].name===name){
                    console.log(jsonData[i].data[0]);
                    const expenses = jsonData[i].data[month_index].expenses;
                    console.log(expenses);
                    setExpenses(expenses);
                }
            }
        }
    }

    function handleExpense(expense){
        return(
            <div>
                <h6>{expense.subject}    {expense.amount}</h6>
            </div>
        )
    }
    fetchData();
    console.log(expenses);


    return (
        <div>
            <Header
                state = {location.state}
            />
            <div className = "express main-section">
                <h1>Expenses</h1>
                <Card/>

                <ul>
                    <li>{expenses.map(handleExpense)}</li>
                </ul>

            </div>
        </div>
    )
}


export default Expenses;
