import React from "react";
import {Link, useNavigate} from 'react-router-dom'

function LoginPage(){
    let loggedIn = false;

    const navigate = useNavigate();

    function checkMonth(data){

    }

    function checkUser(e){
        e.preventDefault();
        console.log("SUbmitted");
        const user = e.target.username.value;
        const password = e.target.password.value;
        async function fetchText() {
            let response = await fetch('http://localhost:8080/127.0.0.1:3100/');
            console.log(response.status); // 200
            console.log(response.statusText); // OK
            if (response.status === 200) {
                let data = await response.text();
                let jsonData = JSON.parse(data);

                for (let i = 0; i < jsonData.length ; i++ ){
                    if(jsonData[i].name===user){
                        if(jsonData[i].password===password){
                            loggedIn = true;
                            const name = user;
                            console.log("Successfully Logged in");
                            const curr_month_index = checkMonth(jsonData);
                            navigate("/home", {state:{name: user, expenses: jsonData[i].data.expenses}});
                        }else{
                            alert("Wrong password, try again");
                        }
                    }
                }
                if (loggedIn===false){
                    console.log("User does not exist. Redirecting to Sign up Page.");
                    navigate("/");
                }
            }
        }
        fetchText();
    }

    return (
        <form onSubmit={checkUser}>
            <h1>Login</h1>
            <input type="text" placeholder="Nickname" name="username"></input>
            <input type="password" placeholder="Password" name="password"></input>
            <button type="submit">Submit</button>
            <Link to="/sign-in">
                <button>Sign Up</button>
            </Link>
        </form>
    )
}

export default LoginPage;
