import { useRef, useEffect } from "react";
import React from "react";
import Header from "./Header";
import {Link, useLocation, useNavigate} from "react-router-dom";

function AddExpense(){
    const ref = useRef(null);



    function handleClick(){
        useEffect(() => {
            const element = ref.current;
            element.style.display="none";
        }, []);
    }


    const location = useLocation();
    const navigate = useNavigate();
    const name = location.state.name;
    const month_index = location.state.month_index;
    async function sendData(e){
        e.preventDefault();
        const newSub = e.target.subject.value;
        const newAmt = e.target.amount.value;
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                "name": name,
                "monthIndex": month_index,
                "subject": newSub,
                "amount": newAmt
            })
        };
        navigate("/expenses", {state:{...location.state}});
        try{
            fetch('http://localhost:8080/127.0.0.1:3100/addexpense', options).then((res)=>{
                console.log(res);
                // let resJson = await res.json();
                if (res.status === 200) {
                    console.log(res.status);
                    console.log("Expense added successfully");
                } else {
                    console.log("Some error occured");
                }
            })
        } catch (err) {
            console.log(err);
        }
    }
    return (
    <div>
        <Header />
        <h1>Add Expense</h1>
        <form onSubmit={sendData}>
            <input type='text' name="subject" placeholder="Name of Expense"></input>
            <input type='number' name="amount" placeholder="Amount"></input>
            <button type="submit">Add</button>
        </form>
        <button onClick={handleClick}>Add Category</button>
        <form ref = {ref} class="form">
            <input type="text" placeholder="Category Name"></input>
            <button className="btn" type="submit"></button>
        </form>
    </div>)
}

export default AddExpense;
