import React from "react";
import Header from "./Header";

function AddExpense(){

    async function sendData(e){
        e.preventDefault();
        const newSub = e.target.subject.value;
        const newAmt = e.target.amount.value;

        try{
            let res = await fetch('http://localhost:3100/addexpense', {
                method: "POST",
                headers: new Headers({"Content-Type": "application/json"}),
                mode: 'no-cors',
                body: JSON.stringify({
                    "subject": "Chips",
                    "amount": "20"
                })
            });
            let resJson = await res.json();
            if (res.status === 200) {
                console.log("User created successfully");
            } else {
                console.log("Some error occured");
            }
        } catch (err) {
            console.log(err);
        }
    }

    return (
    <div>
        <Header />
        <h1>Add Expense</h1>
        <form onSubmit={sendData}>
            <input type='text' name="subject" placeholder="Name of Expense"></input>
            <input type='number' name="amount" placeholder="Amount"></input>
            <button type="submit">Add</button>
        </form>
    </div>)
}

export default AddExpense;
