import React, {useState} from "react";
import Header from "./Header";
import { useLocation} from "react-router";
import { Link } from "react-router-dom";

function Savings(){
    const location = useLocation();
    const name = location.state.name;
    const month_index = location.state.month_index;
    const [index, setIndex] = useState({sections: []})
    const [remainder, setRemainder] = useState(0);

    async function fetchData() {
        let response = await fetch("http://localhost:8080/127.0.0.1:3100/");
        if (response.status === 200) {
            let data = await response.text();
            let jsonData = JSON.parse(data);
            for (let i = 0; i < jsonData.length ; i++ ){
                if(jsonData[i].name===name){
                    const index = jsonData[i].data[month_index].index;
                    setIndex(index);
                    setRemainder(index.total);
                    break;
                }
            }
        }
    }

    function handleSection(section){
        const amount = section.amount;
        setRemainder((prev)=>{
            return (prev-amount)
        });

        return(
            <tr>
                <td>{section.name}</td>
                <td>{section.amount}</td>
            </tr>
        )
    }
    fetchData();


    return (
        <div>
            <Header
                state={{...location.state}}
            />
            <div className="main-section">
                <h1>Savings</h1>
                <h5>Money in account is {index.total}</h5>

                <table>
                    <tbody>
                        <tr>
                            <th>Name</th>
                            <th>Amount</th>
                        </tr>
                        {index.sections.map(handleSection)}
                    </tbody>
                </table>

                <Link to="/addsection" state={{...location.state}}>
                    <button className="btn lg primary">Add Section</button>
                </Link>
            </div>

        </div>
    )
}

export default Savings;
