import { useState } from "react";
import React from "react";
import Header from "./Header";
import {Link, useLocation, useNavigate} from "react-router-dom";

function AddExpense(){
    let num_of_sections = 1;
    const location = useLocation();
    const navigate = useNavigate();
    const name = location.state.name;
    const month_index = location.state.month_index;
    const [index, setIndex] = useState({sections: [], categories: []});
    const [input, setInput] = useState([{key:1, section:'', amount:''},{key:2, section:'', amount:''}]);

    async function fetchData(){
        let response = await fetch('http://localhost:8080/127.0.0.1:3100/');
        if (response.status === 200) {
            let data = await response.text();
            let jsonData = JSON.parse(data);
            for (let i = 0; i < jsonData.length ; i++ ){
                if(jsonData[i].name===name){
                    const index = jsonData[i].data[month_index].index;
                    setIndex(index);
                }
            }
        }
    }
    fetchData();

    function handleSection(section){
        const each_input_key = input.slice(-1).key;
        const name_of_section = "section" + each_input_key;
        return(
            <div>
                <input type="radio" id="html" name={name_of_section} value={section.name}></input>
                <label for="html">{section.name} ( {section.amount} left. )</label><br />
            </div>
        )
    }
    function handleAddClick(e){
        e.preventDefault();
        console.log("hahaha");
        // setInput((prev)=>{
        //     const new_key = prev.slice(-1).key + 1;
        //     // prev.push({key:new_key, section:'', amount:''});
        //     console.log(new_key);
        // })
    }

    function handleInput(each_input){
        const name_of_section = "section" + each_input.key;

        return(
            <div>
                {index.sections.map(handleSection)}
                <input type='number' name={name_of_section + "-amount"} placeholder="Amount"></input>
            </div>
        )
    }
    function handleHalfSection(section){
        const name_of_section = "section" + {num_of_sections};
        return(
            <div>
                <input type="radio" id="html" name={name_of_section} value={section.name}></input>
                <label for="html">{section.name} ( {section.amount} left. )</label><br />
            </div>
        )
    }
    function handleCategory(category){
        return(
            <div>
                <input type="radio" id="html" name="category" value={category}></input>
                <label for="html">{category}</label><br />
            </div>
        )
    }
    async function sendData(e){
        e.preventDefault();
        const newSub = e.target.subject.value;
        const newAmt = e.target.amount.value;
        const section = e.target.section.value;
        const category = e.target.category.value;

        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                "name": name,
                "monthIndex": month_index,
                "subject": newSub,
                "amount": newAmt,
                "section": section,
                "category": category
            })
        };
        navigate("/expenses", {state:{...location.state}});
        try{
            fetch('http://localhost:8080/127.0.0.1:3100/addexpense', options).then((res)=>{
                console.log(res);
                // let resJson = await res.json();
                if (res.status === 200) {
                    console.log(res.status);
                    console.log("Expense added successfully");
                } else {
                    console.log("Some error occured");
                }
            })
        } catch (err) {
            console.log(err);
        }
    }

    return (
    <div>
        <Header />
        <h1>Add Expense</h1>
        <form onSubmit={sendData}>
            <input type='text' name="subject" placeholder="Name of Expense"></input>
            <h4>Section</h4>
            {input.map(handleInput)}
            <button type="button" onClick={handleAddClick}>Add Section</button>
            <h4>Categories</h4>
            {index.categories.map(handleCategory)}
            <button type="submit">Add</button>
        </form>

        <h3> Add Category </h3>
        <form>
            <input type="text" placeholder="Category Name"></input>
            <button className="btn" type="submit">Add Category</button>
        </form>
    </div>)
}

export default AddExpense;
