import React from "react";
import Header from "./Header";

function AddExpense(){

    async function sendData(e){
        let res = await fetch('http://localhost:3100/addexpense', {
            method: "POST",
            body: JSON.stringify({
                subject: e.target.subject,
                amount: e.target.amount
            }),
        });
        await res.json();
    }

    return (
    <div>
        <Header />
        <h1>Add Expense</h1>
        <form onSubmit={sendData}>
            <input type='text' name="subject" placeholder="Name of Expense"></input>
            <input type='number' name="amount" placeholder="Amount"></input>
            <button type="submit">Add</button>
        </form>
    </div>)
}

export default AddExpense;
