import { useState } from "react";
import React from "react";
import Header from "./Header";
import {Link, useLocation, useNavigate} from "react-router-dom";

function AddExpense(){

    const location = useLocation();
    const navigate = useNavigate();
    const name = location.state.name;
    const month_index = location.state.month_index;
    const [index, setIndex] = useState({sections: [], categories: []});

    let promise = new Promise(async (res, rej)=>{
        let response = await fetch('http://localhost:8080/127.0.0.1:3100/');
        console.log(response.status); // 200
        console.log(response.statusText); // OK
        if (response.status === 200) {
            let data = response.text();
            let jsonData = JSON.parse(data);
            for (let i = 0; i < jsonData.length ; i++ ){
                if(jsonData[i].name===user){
                    const index = jsonData[i].data[month_index].index;
                    resolve(index);
                }
            }
        }
    });
    promise.then((res)=>{
        setIndex(res);
    })

    function handleSection(section){
        return(
            <li><a class="dropdown-item" href="#">{section.name}</a></li>
        )
    }

    async function sendData(e){
        e.preventDefault();
        const newSub = e.target.subject.value;
        const newAmt = e.target.amount.value;
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                "name": name,
                "monthIndex": month_index,
                "subject": newSub,
                "amount": newAmt
            })
        };
        navigate("/expenses", {state:{...location.state}});
        try{
            fetch('http://localhost:8080/127.0.0.1:3100/addexpense', options).then((res)=>{
                console.log(res);
                // let resJson = await res.json();
                if (res.status === 200) {
                    console.log(res.status);
                    console.log("Expense added successfully");
                } else {
                    console.log("Some error occured");
                }
            })
        } catch (err) {
            console.log(err);
        }
    }


    return (
    <div>
        <Header />
        <h1>Add Expense</h1>
        <form onSubmit={sendData}>
            <input type='text' name="subject" placeholder="Name of Expense"></input>
            <input type='number' name="amount" placeholder="Amount"></input>
            <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                       Select Section
                    </button>
                    <ul class="dropdown-menu">

                        {index.sections.map(handleSection)};
                    </ul>
            </div>
            <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                       Select Section
                    </button>
                    <ul class="dropdown-menu">

                        {index.sections.map(handleSection)};
                    </ul>
            </div>
            <button type="submit">Add</button>

        </form>
        <h3> Add Category </h3>
        <form>
            <input type="text" placeholder="Category Name"></input>
            <button className="btn" type="submit">Add Category</button>
        </form>
    </div>)
}

export default AddExpense;
